test('code with immutable in solidity', () => {
  // Via https://twitter.com/RenanRSouza35/status/1644308118375915523
  /*
    contract Foo {
        uint public immutable a;

        constructor(uint _a) {
            a = _a;
        }
    }
  */
  const code = "";
  const r = selectorsFromBytecode(code);
  r.sort();

  expect(r).toEqual([
    "0x0dbe671f" // a()
  ]);
})

